import React from 'react';
import {View, Text, SafeAreaView, TextInput, ScrollView, StyleSheet, FlatList, Image,  TouchableOpacity, LayoutAnimation, ShadowPropTypesIOS} from 'react-native'
import * as firebase from "firebase"
import {Ionicons} from '@expo/vector-icons'
import moment from 'moment'
import db from "firebase"
import Fire from '../Fire'
export default class JoinProject extends React.Component {


   constructor() {
       super()
        this.fillProjects()
       this.state = {
        projects: {},
        }
   }

    fillProjects = () => {
        const projects = {};
        const db = firebase.firestore();
   //    .log(firebase.database().ref('posts').orderByValue());

        const onReceive = (querySnapshot) => {
            querySnapshot.forEach(function(doc) {
                // doc.data() is never undefined for query doc snapshots
                projects[doc.id] = doc.data();
            });
            this.setState({projects: projects})
        }
        db.collection("projects").get()
            .then(onReceive.bind(this));

    }
    handleJoin = projectId => {
        console.log("This is the uid", projectId)
        Fire.shared.getUserData(firebase.auth().currentUser.email).then(({id, user}) => {
            const arr = user["projects"] 
            arr.push(projectId)

             Fire.shared.database().ref('users/' + id).set({
                projects: arr
              });
        }) 

    }
    renderProject = projectId => {

         const project = this.state.projects[projectId]
       // const ref = firebase.storage().ref(post.image);
        //const url =  ref.getDownloadURL();
        return (

            <View style = {styles.feedItem}>
                {/* <Image source={post.avatar} style = {styles.avatar}></Image> */}
                <View style = {{flex: 1}}>
                    <View style = {{flexDirection: 'row', justifyContent: "space-between",alignItems: 'center'}}>
                        <View>

                            <Text style = {styles.name}>{project.title}</Text>

                        </View>

                    </View>
                    <Text style = {styles.descrip}>{project.descrip}</Text>
                     {/* <Image source = {{Image_Http_URL }} style = {styles.postImage} resizeMode = "cover"/>  */}

                  <TouchableOpacity onPress ={() => this.handleJoin(projectId)}><Text>Join this project</Text></TouchableOpacity>

                </View>


            </View>
        )
    }
    render() {
        LayoutAnimation.easeInEaseOut()

        return (

            <View style = {styles.container}>
                <View style = {styles.header}>
                    <Text style = {styles.headerTitle}>Join a project</Text>

                </View>

                <FlatList
                    style={styles.feed} 
                    data={Object.keys(this.state.projects)} 
                    renderItem={({ item }) => this.renderProject(item)} 
                    keyExtractor = {item => item.id}    
                    showsVerticalScrollIndicator = {false}  
                /> 
               {/* <View style = {{width: 15}}>
                <TouchableOpacity style = {{backgroundColor: "lightgrey", position: "fixed", width: 24, height: 44, borderRadius: 16, alignItems: 'center', alignContent: 'center'}}>
                   <Ionicons name = "ios-add" onPress ={() => this.props.navigation.navigate("CreatePost")} style = {{alignSelf: 'center'}} size = {32} color = "black"></Ionicons>
                </TouchableOpacity>  
                </View> */}

            </View>

                /* <TouchableOpacity style ={{marginTop: 32}} onPress = {this.signOutUser}>
                    <Text>Logout</Text>
                </TouchableOpacity> */

        )
    }
}

const styles = StyleSheet.create({
    container: {
       backgroundColor: "#EFECF4",
       flex: 1,
    },
    header: {
       paddingTop: 16,
        paddingBottom: 16,
        backgroundColor: "#FFF",
        alignItems: 'center',
        justifyContent: 'center',
        borderBottomWidth: 1, 
        borderBottomColor: "#EBECF4",
        flexDirection: "row",

    },
    headerTitle: {
        fontSize: 20,
        fontWeight: "500", 
        alignSelf: 'center' 
    },
    feed: {
         marginHorizontal: 16
    },
    feedItem: {
        backgroundColor: "#FFF",
        borderRadius: 5,
        padding: 8,
        flexDirection: 'row',
        marginVertical: 8
    },
    avatar: {
        width: 36,
        height: 36,
        borderRadius: 18,
        marginRight: 16
    },
    name: {
        fontSize: 15,
        fontWeight: "500",
        color: "#454D65"
    },
    timestamp: {
        fontSize: 11,
        color: '#C4C6CE',
        marginTop: 4,
    },
    descrip: {
        marginTop: 16,
        fontSize: 14,
        color: "#838899"
    },
    postImage: {
        width: undefined,
        height: 150,
         borderRadius: 5,
         marginVertical: 16
    },
    image: {
        width: 500,
        height: 500
    },
    back: {
        width: 32,
        height: 32,
        borderRadius: 21,
        alignItems: 'center',
        backgroundColor: "rgba(21,22,48,0.1)",
        justifyContent: 'center'
    },


})
